import '../../style/components/layout/Presentacion.css'

function Presentacion ({text, children}) {
var length = text.length;
var deg = 360/length;

    return (
        <div className='spinning-text-wrapper'>
           <div className='spinning-text'>
               <p>{text.split("").map((letra, i) => (
               <span className='span'
                   key={i}
                   style= {{
                       transform: ` rotate (${12 * i} deg)` }}> 
                     {letra}
               </span>))}</p>
           </div>
           {children}
        </div>
    )
}

export default Presentacion;
